        for (let c = 0, cLen = cell.length, rs, cs; c < cLen; c++) {
          rs = cell[c].rowSpan;
          cs = cell[c].colSpan;
          if (rs < 2 && cs < 2) continue;

          if (rs + i > rowIndex && rowIndex > i) {
