      const rows = contextTable._trElements;
      let rowSpanArr = [];
      let spanIndex = [];
      let passCell = 0;
      const removeCell = [];
      const removeSpanArr = [];

      for (let i = 0, len = contextTable._rowCnt, row, insertIndex, cells, newCell, applySpan, cellColSpan; i < len; i++) {
        row = rows[i];
