            removeIndex = c + cellColSpan;

            if (spanIndex.length > 0) {
              const lastCell = !cells[c + 1];

              for (let r = 0, arr; r < spanIndex.length; r++) {
                arr = spanIndex[r];
                if (arr.row > i) continue;

                if (removeIndex >= arr.index) {
                  cellColSpan += arr.cs;
